import { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import './styles.css';

const Signup = () => {
  const [data, setData] = useState({ firstName: "", email: "", password: "", latitude: "", longitude: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [google, setGoogle] = useState(null);

  // Load Google Maps script and set it up
  useEffect(() => {
    if (window.google) {
      setGoogle(window.google);
    } else {
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_GOOGLE_MAPS_API_KEY&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = () => setGoogle(window.google);
      document.body.appendChild(script);
    }
  }, []);

  const getLocation = () => {
    if (!google) {
      setError("Google Maps API is not loaded yet.");
      return;
    }

    const geocoder = new google.maps.Geocoder();
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const lat = position.coords.latitude;
          const lng = position.coords.longitude;
          setData({ ...data, latitude: lat, longitude: lng });

          // Reverse Geocode to get the address
          const latLng = new google.maps.LatLng(lat, lng);
          geocoder.geocode({ location: latLng }, (results, status) => {
            if (status === "OK" && results[0]) {
              // Optionally, use address data here if needed
              console.log("Address: ", results[0].formatted_address);
            } else {
              setError("Geocoder failed due to: " + status);
            }
          });
        },
        () => {
          setError("Failed to get location.");
        }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  };

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = "http://localhost:3000/api/users/signup";
      const { data: res } = await axios.post(url, data);
      navigate("/login");
    } catch (error) {
      if (error.response) setError(error.response.data.message);
    }
  };

  return (
    <div className="signup_container">
      <div className="signup_form_container">
        <div className="left">
          <h1>Welcome Back</h1>
          <Link to="/login">
            <button type="button" className="white_btn">
              Sign in
            </button>
          </Link>
        </div>
        <div className="rightl">
          <form className="form_container" onSubmit={handleSubmit}>
            <input
              className="input"
              type="text"
              name="firstName"
              placeholder="First Name"
              onChange={handleChange}
              required
            />
            <input
              className="input"
              type="email"
              name="email"
              placeholder="Email"
              onChange={handleChange}
              required
            />
            <input
              className="input"
              type="password"
              name="password"
              placeholder="Password"
              onChange={handleChange}
              required
            />
            <button
              className="green_btn"
              type="button"
              onClick={getLocation}
            >
              Get Location
            </button>
            <button className="green_btn" type="submit">
              Sign Up
            </button>
          </form>
          {error && <div className="error_msg">{error}</div>}
        </div>
      </div>
    </div>
  );
};

export default Signup;
